import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Post from './components/Post';
import Story from './components/Story';
import './App.scss'
import './App.css'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

import firestore from './firebase';

export const web_data = React.createContext(null)

const Mat = () => {
  const [story, setStory] = useState(0);
  const data = {
    story: [story, setStory]
  }
  const [posts, setPosts] = useState([]);
  const [story_data, setStory_data] = useState();
  let [stt, setStt] = useState(true);

  const getPosts = () => {
    firestore.collection('posts').onSnapshot((shot) => {
      const allPost = [];
      shot.forEach((cur) => allPost.push(cur.data()));

      setPosts(allPost);
    }, (error) => console.error(error))
  }

  const getStories = () => {
    firestore.collection('stories').onSnapshot((shot) => {
      const allStories = [];
      shot.forEach((cur) => allStories.push(cur.data()));

      setStory_data(allStories);
    }, (error) => console.error(error))
  }

  useEffect(() => {
    getPosts();
    getStories();
  }, []);

  if (story === 3) {
    setStt(true);
  }

  return (
    <web_data.Provider value={data}>
      <Router>
        <div className='row'>
          {
            stt === true ?
              <div>
                <Header />
                <div className='main row col l8 offset-l2'>
                  <div className='row soty_bar_mid col m10 s12 offset-m1 hide-on-large-only'>
                    <div className='story_box_mid'>
                      <div className="stories" onClick={() => { setStt(!stt); setStory(0) }}>
                        <img className='circle profile' src='https://upload.wikimedia.org/wikipedia/commons/thumb/3/3d/DC_Comics_logo.svg/1200px-DC_Comics_logo.svg.png'></img>
                        <p className='black-text center'>BatMan</p>
                      </div>
                      <div className="stories" onClick={() => { setStt(!stt); setStory(1) }}>
                        <img className='circle profile' src='https://instagram.fuln2-1.fna.fbcdn.net/v/t51.2885-19/s150x150/76844870_2735946593133365_7586026101554544640_n.jpg?_nc_ht=instagram.fuln2-1.fna.fbcdn.net&_nc_ohc=AkHYh2kTbpUAX9EOlvH&oh=b55ee92cab6f1bcc74dd5f39a66a668b&oe=5EBDABCA'></img>
                        <p className='black-text center'>aniKod</p>
                      </div>
                      <div className="stories" onClick={() => { setStt(!stt); setStory(2) }}>
                        <img className='circle profile' src='https://i.pinimg.com/originals/f2/a8/3b/f2a83b69dad12688b47dbf2bb12b8932.jpg'></img>
                        <p className='black-text center'>Marshmello</p>
                      </div>
                    </div>
                  </div>
                  {posts.map((cur, index) => <Post avatar={cur.pro} name={cur.name} image={cur.img} time={index} />)}
                  <div className='row col l4 side hide-on-med-and-down fixed'>
                    <nav className='z-depth-0'>
                      <div className="nav-wrapper white box">
                        <a className="brand-logo show-on-small left">
                          <img className='circle profile' src='https://instagram.fuln2-1.fna.fbcdn.net/v/t51.2885-19/s150x150/76844870_2735946593133365_7586026101554544640_n.jpg?_nc_ht=instagram.fuln2-1.fna.fbcdn.net&_nc_ohc=AkHYh2kTbpUAX9EOlvH&oh=b55ee92cab6f1bcc74dd5f39a66a668b&oe=5EBDABCA'></img>
                          <h6 className='black-text'>Soulforger</h6>
                        </a>
                      </div>
                      <div className='story_box'>
                        <h6 className="grey-text">stories</h6>
                        <div className="stories" onClick={() => { setStt(!stt); setStory(0) }}>
                          <img className='circle profile' src='https://upload.wikimedia.org/wikipedia/commons/thumb/3/3d/DC_Comics_logo.svg/1200px-DC_Comics_logo.svg.png'></img>
                          <p className='black-text'>BatMan</p>
                        </div>
                        <div className="stories" onClick={() => { setStt(!stt); setStory(1) }}>
                          <img className='circle profile' src='https://instagram.fuln2-1.fna.fbcdn.net/v/t51.2885-19/s150x150/76844870_2735946593133365_7586026101554544640_n.jpg?_nc_ht=instagram.fuln2-1.fna.fbcdn.net&_nc_ohc=AkHYh2kTbpUAX9EOlvH&oh=b55ee92cab6f1bcc74dd5f39a66a668b&oe=5EBDABCA'></img>
                          <p className='black-text'>aniKod</p>
                        </div>
                        <div className="stories" onClick={() => { setStt(!stt); setStory(2) }}>
                          <img className='circle profile' src='https://i.pinimg.com/originals/f2/a8/3b/f2a83b69dad12688b47dbf2bb12b8932.jpg'></img>
                          <p className='black-text'>Marshmello</p>
                        </div>
                      </div>
                    </nav>
                  </div>
                </div>
              </div> :
              <div>
                <Story avatar={story_data[story].pro} name={story_data[story].name} image={story_data[story].img} />
                <a className="btn exit_button" onClick={() => { setStt(!stt) }}>
                  <i className="material-icons" style={{ fontSize: "30px" }}>close</i>
                </a>
              </div>
          }
        </div >
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
        </Switch>
      </Router>
    </web_data.Provider>
  );
}

export default Mat;
