import React, { useEffect, useRef, useState } from 'react';
import './story.scss'
import M from 'materialize-css/dist/js/materialize.min.js'

const Story = (nani) => {
    const [widthstate, setWidthState] = useState(0);
    const [number, setNumber] = useState(0);
    const avatar = nani.avatar;
    const name = nani.name;
    const image = nani.image;

    let [instance, setInstance] = useState(null);

    const carouselRef = useRef(null);

    useEffect(() => {
        setInstance(M.Carousel.init(carouselRef.current, {
                    fullWidth: true,
                    noWrap: true,
                    indicators: true,
                    duration: 90
                }))

        return () => {
            // instance.destroy();
        }
    }, [])

    if (image.length > 1) {
        var slide = image.map((cur, i) => (<a className="carousel-item" key={i}><img className="image" src={cur} /></a>))
        slide = (
            <div ref={carouselRef} className="carousel carousel-slider">
                {slide}
                <a className="button btn arrow transparent" tabIndex="-1" onClick={() => { instance.prev(); setNumber(number - 1); setWidthState(-10) }}></a>
                <a className="btn arrow transparent button_right" tabIndex="-1" onClick={() => { instance.next(); setNumber(number + 1); setWidthState(-10) }}></a>
            </div>
        )
    } else
        var slide = (<a className="img"><img className="image" src={image} /></a>)


    const getComplete = (i) => {
        if (i == number) {
            if (widthstate == 100) {
                setNumber(number + 1);
                setWidthState(0);
                instance.next();
            }
            return widthstate;
        }
        if (i < number)
            return 100;

        return 0;
    }


    useEffect(() => {
        const load = () => {
            setWidthState((old) => {
                return Math.min(old + 1, 100)
            })
        }

        const timer = setInterval(load, 100)

        return () => {
            clearInterval(timer);
        }
    })

    const progress_loader = image.map((cur, i) => (
        <div className="progress" key={i}>
            <div className="determinate" key={i} id={"loader" + i} style={{ width: getComplete(i) + '%' }}></div>
        </div>
    ))

    return (
        <div className='story_back'>
            <div className='story hide-on-med-and-down'>
                <ul className="collection with-header">
                    <li className="collection-item avatar">
                        <img className='circle profile' src={avatar}></img>
                        <div className="text">
                            <h6 className='h5'>{name}</h6>
                            <h6>1h</h6>
                        </div>
                        <a href="#!" className="secondary-content"><i className="material-icons white-text" style={{ fontSize: "30px" }}>more_horizs</i></a>
                    </li>
                </ul>
                <div className="progress_container">
                    {progress_loader}
                </div>
                {slide}
            </div>
        </div>
    )
}

export default Story;
