import React from 'react';

const Post = (nani) => {
    const avatar = nani.avatar;
    const name = nani.name;
    const image = nani.image;
    if (typeof image == 'object'){
        var slide = image.map((cur) => (<a class="carousel-item" href="#one!"><img src={cur} /></a>))
        slide = (
            <div class="carousel carousel-slider">
                {slide}
            </div>
        )
    }else 
        var slide = (<a class="img" href="#one!"><img src={image} /></a>)
    const M = window.M;

    document.addEventListener('DOMContentLoaded', function () {
        var elems = document.querySelectorAll('.carousel');
        var instances = M.Carousel.init(elems, {
            fullWidth: true,
            noWrap: true,
            indicators: true,
            dist: -100
        });
    });

    return (
        <div className='post col l6 offset-l2 s12 m10 offset-m1'>
            <nav>
                <div className="nav-wrapper white">
                    <a href="#" className="brand-logo show-on-small left">
                        <img className='circle profile' src={avatar}></img>
                        <h6 className='black-text'>{name}</h6>
                    </a>
                    <ul id="nav-mobile" className="right black-text">
                        <li>
                            <a href='#' className="waves-effect waves-light black-text">
                                <i className='material-icons'>more_horizs</i>
                            </a>
                        </li>
                    </ul>
                </div>
            </nav>
            <div className="card">
                <div className="card-image">
                    {slide}
                </div>
                <div className="card-content">
                    <div>
                        <a href='#'>
                            <i className='material-icons small black-text'>favorite_border</i>
                        </a>
                        <a href='#'>
                            <img src='https://image.flaticon.com/icons/png/512/1947/1947247.png' className='image' />
                        </a>
                        <a href='#'>
                            <i className='material-icons small black-text'>near_me</i>
                        </a>
                        <a href='#'>
                            <img src='https://www.flaticon.com/premium-icon/icons/svg/1947/1947270.svg' className='image right' />
                        </a>
                    </div>
                    <h7>
                        Neque porro quisquam est qui dolorem ipsum quia dolor sit amet, consectetur, adipisci velit...
                        </h7>
                </div>
                <div className="card-action">
                    <nav>
                        <div class="nav-wrapper">
                            <form>
                                <div class="input-field">
                                    <input id="comment" type="search" required />
                                    <label for="comment">Add a comment</label>
                                    <i className="material-icons close">send</i>
                                </div>
                            </form>
                        </div>
                    </nav>
                </div>
            </div>
        </div>
    );
}

export default Post;
